                $('#error_message').html("L'email est vide");
            }
            if (!myRegex.test(mail)) {
                $('#error_message').html("Votre mail est invalide");
            }
            else {
                $('#error_message').html('');
                $.ajax({
                    url: "artiste.php",
                    method: "POST",
                    data: {mail: mail},
